import React from 'react';
import CollectionOverview from '../../components/collection-overview/collection-overview.component';
import {fetchCollectionStartAsync} from '../../redux/shop/shop.action';
import {Route, Routes} from 'react-router-dom';
import Category from '../category/category.component';
import { connect } from 'react-redux';
import WithSpinner from '../../components/with-spinner/with-spinner.component';
import { createStructuredSelector } from 'reselect';
import { selectIsFetching,selectIsCollectionEmpty } from '../../redux/shop/shop.selector';

const CollectionOverviewWithSpinner = WithSpinner(CollectionOverview);
const CategoryWithSpinner = WithSpinner(Category);

class ShopPage extends React.Component{

    async componentDidMount(){
        const {fetchCollectionStartAsync} = this.props;
        fetchCollectionStartAsync();
    }

    render(){ 
        const {collectionEmpty} = this.props;
        return(
            <div>
            <Routes>
                <Route exact path="/" element={<CollectionOverviewWithSpinner isLoading={!collectionEmpty}/>} />
                <Route path=":category" element={<CategoryWithSpinner isLoading={!collectionEmpty} />} />
            </Routes>
            </div>)
    }
}

const mapStatetoProps = createStructuredSelector({
    isLoading :selectIsFetching,
    collectionEmpty: selectIsCollectionEmpty
})

const mapDispatchtoProps = dispatch =>({
    fetchCollectionStartAsync: collection=> dispatch(fetchCollectionStartAsync(collection))
  })

export default connect(mapStatetoProps,mapDispatchtoProps)(ShopPage);
