import React, { useEffect } from 'react';
import "./collection.styles.scss"
import CollectionItem from "../../components/collection-item/collection-item.component"
import {connect} from "react-redux";
import {selectCollection} from '../../redux/shop/shop.selector';
import { useParams } from "react-router-dom";
import { changeParam } from "../../redux/shop/shop.action";

const Category = ({collection,changeParam})=>{
    const name = useParams().category;
    useEffect(() => {
        changeParam(name);
      });
    return(  
    <div className="collection-page">
        <div className="title">{name}</div>
        {console.log(collection)}
        <div className="items"> {collection.map(({id, ...info})=><CollectionItem key={id} item={info}/>)}</div>
    </div>)
}

const mapStateToProps = state => ({
    collection: selectCollection(state.shop.params)(state),
  });

const mapDispatchToProps = dispatch => ({
    changeParam: item => dispatch(changeParam(item)),
  });

export default (connect)(mapStateToProps,mapDispatchToProps)(Category);
