import React from 'react';
import CollectionOverview from '../../components/collection-overview/collection-overview.component';
import {Route, Routes} from 'react-router-dom';
import Category from '../category/category.component';
import {firestore, getCollection} from "../../firebase/firebase.utils"
import { connect } from 'react-redux';
import {updateData} from "../../redux/shop/shop.action"
import WithSpinner from '../../components/with-spinner/with-spinner.component';

const CollectionOverviewWithSpinner = WithSpinner(CollectionOverview);
const CategoryWithSpinner = WithSpinner(Category);

class ShopPage extends React.Component{
    constructor(){
        super();
        this.state = {
            isLoading:true
        }
    }

    async componentDidMount(){
        const {updateData} = this.props;
        const collection = await getCollection(firestore.collection("collections"));
        updateData(collection);
        // console.log(collection);
        this.setState({isLoading:false})
    }

    render(){ 
        const {isLoading} = this.state;
        return(
            <div>
            <Routes>
                <Route exact path="/" render={()=><CollectionOverviewWithSpinner isLoading={isLoading}/>} />
                <Route path=":category" element={<CategoryWithSpinner isLoading={isLoading} />} />
            </Routes>
            </div>)
    }
}

const mapDispatchtoProps = dispatch =>({
    updateData: collection=> dispatch(updateData(collection))
  })

export default connect(null,mapDispatchtoProps)(ShopPage);
