export const updateCart = (cart,newItem) =>{
    console.log(cart);
    console.log(newItem);
    cart.map(item=> {
        console.log(item)
        console.log(newItem.id)
        if (item.id ===newItem.id){
            return [...cart,
                {id:item.id, quantity:item.quantity+1}]
            }
        })
    return [...cart ,{[newItem.id]:1}]

}
