export const updateCart = (cart,newItem) =>{
    console.log(cart);
    console.log(newItem);
    cart.map(item=> {
        if (item.id ===newItem.id){
            console.log("yes")
            return [...cart,
                {[item.id]:item.quantity+1}]
            }
        })
    return [...cart ,{[newItem.id]:1}]

}
