export const updateCart = (cart,newItem) =>{
    cart.map(item=> {
        if (item.id ===newItem.id){
            console.log("yes")
            return [...cart,
                {newItem, quantity:[item.quantity]+1}]
            }
        })
    return [...cart ,{id:newItem.id,quantity:1}]

}
