import React from 'react';
import CollectionOverview from '../../components/collection-overview/collection-overview.component';
import {Route, Routes} from 'react-router-dom';
import Category from '../category/category.component';
import {firestore, getCollection} from "../../firebase/firebase.utils"
import { connect } from 'react-redux';
import {updateData} from "../../redux/shop/shop.action"

class ShopPage extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            isLoading:false
        }
    }

    async componentDidMount(){
        const collection = await getCollection(firestore.collection("collections"));
        updateData(collection);
        // console.log(collection);
        this.setState({isLoading:true})
    }

    render(){ 
        return(
            <div>
            <Routes>
                <Route exact path="/" element={<CollectionOverview />} />
                <Route path=":category" element={<Category />} />
            </Routes>
            </div>)
    }
}

const mapDispatchtoProps = dispatch =>({
    updateData: collection=> dispatch(updateData(collection))
  })

export default connect(null,mapDispatchtoProps)(ShopPage);
